}

/* Tooltip text */
.tooltip .tooltiptext {
    visibility: hidden;
    width: auto;
    background-color: transparent;
    color: #fff;
    text-align: center;
    padding: 0;
    border-radius: 0;
    position: absolute;
    z-index: 1;
    display: block;
    left: 90px;
    width: 225px;
}

.tooltip1 .tooltiptext1 {
    visibility: hidden;
    background-color: #fff;
    color: #000;
    text-align: left;
    border-radius: 0;
    position: absolute;
    z-index: 1;
    display: block;
    left: 256px;
    border: 2px solid #BBB6AE;
    padding: 8px;
    width: 300px;
    top: -4px;
}

.tooltip1 .tooltiptext1 li {
    font-size: 14px;
}

.tooltip2 .tooltiptext2 {
    visibility: hidden;
    background-color: #e8e8e8;
    color: #000;
    text-align: left;
    border-radius: 0;
    position: absolute;
    z-index: 1;
    display: block;
    left: 224px;
    padding: 12px;
    width: 200px;
    top: -56px;
    font-size: 12px;
}
.tooltip2 .tooltiptext2 
ul {
    margin: 0 0 10px 20px;
}

/* Show the tooltip text when you mouse over the tooltip container */
.tooltip:hover .tooltiptext {
    visibility: visible;
}

