white.scss:
/* Color: White */

body {
    background: #fff;
    color: #606060;
}

a {
    color: #22448b;

    font-weight: normal;
    text-decoration: underline;
}

a:hover {
    color: #9C1B21;
}

h2 {
    color: #084586;
    font-family: 'Open Sans', arial, helvetica, sans-serif;
    font-weight: normal;
}

h3 {
    color: #9C1B21;
    font-family: 'Open Sans', arial, helvetica, sans-serif;
    font-weight: normal;
}

div.relativeContainer {
    position: relative;
}

.noShow {
    display: none;
}

#TopMenu {
    background: #fff;
}

#TopMenu li a {
    color: #000;
}

#TopMenu li div {
    color: #000;
}

#TopMenu li a:hover {
    text-decoration: underline;
}

#Menu {
    background: #757575;
    font-family: 'Open Sans', arial, helvetica, sans-serif;
}

#Menu li a {
    border-right: 1px solid #fff;
    color: #fff;
}

#Menu li a:hover {
    background: #606060;
}

#Menu li ul li a {
    background-image: none;
    background-color: #757575;
}

#Outer {
    background: #fff;
}

#SearchForm input {
    border-radius: 12px;
    padding: 5px 5px 5px 25px;
    z-index: 5;
    position: absolute;
    font-size: 15px;
    width: 260px;
    color: #1e1e1e;
    padding: 1px 3px 0px 8px;
    max-height: 30px;
    height: 30px;
    border: none;
}

#SearchForm .Button {
    border: 0;
}

#SearchForm a {
    color: #cfcfcf;
}

#SearchForm p {
    color: #cfcfcf;
}

.Left #SideCategoryList {
    background: #f4f4f4;
    border: 1px solid #dadada;
}

.Left #SideCategoryList a {
    color: #000;
}

.Left #SideCategoryList a:hover {
    color: #0066cc;
}

.sliderOverlay {
    position: absolute;
    z-index: 1;
    font-size: 24px;
}

.sliderOverlay div {
    position: absolute;
    top: 20px;
    right: 10px;
}

.sliderOverlay p {
    font-size: 1em;
    font-weight: 600;
    font-style: italic;
    color: #084586;
    text-align: center;
}

.sliderOverlay ul {
    list-style-type: none;
    margin-bottom: 0px;
}

.sliderOverlay li {
    font-size: 0.8em;
    line-height: 1.6;
    font-weight: 700;
    color: #000;
}

.sliderOverlay li:before {
    content: 'âœ“';
    margin-left: -1.4em;
    margin-right: .200em;
    color: #9C1B21;
    font-weight: 600;
    font-size: 1em;
}

.sliderOverlay p:last-child {
    font-size: 0.5em;
    font-style: normal;
    text-align: left;
    //padding-left: 20px;
}

#Footer {
    background: #f4f4f4;
    border: 1px solid #dadada;
}

.NewsletterSubscription input {
    border: 1px solid #dadada;
}

.ProductList strong,
.ProductList strong a {
    color: #005DAB;
}

.ProductList strong a:hover {
    color: #9C1B21;
}

.ProductPrice,
.ProductList em {
    color: #ff3941;
}

.ProductList .Rating {
    color: #9a9a9a;
}

.ProductActionAdd a,
.ProductActionAdd strong a {
    color: #005DAB;
}

.ProductActionAdd a:hover,
.ProductActionAdd strong a:hover {
    color: #9C1B21;
}

.Right .TopSellers li .TopSellerNumber {
    /* Contains the number of the item */
    font-family: "Times New Roman", Times, serif;
    background: #98C100;
    color: #fff;
}

.Breadcrumb {
    border-bottom: 1px solid #dadada;
}

.CompareItem {
    width: 200px;
    border-left: solid 1px #dadada;
}

.CompareFieldName {
    font-weight: bold;
    border-bottom: 1px solid #dadada;
}

.CompareFieldTop {
    border-bottom: solid 1px #dadada;
}

.CompareFieldTop a:hover {
    color: #ff3941;
}

.CompareCenter {
    border-left: solid 1px #dadada;
    border-bottom: solid 1px #dadada;
}

.CompareLeft {
    border-left: solid 1px #dadada;
    border-bottom: solid 1px #dadada;
}

.ViewCartLink {
    background: url('//cdn3.bigcommerce.com/r-4f615c2ae007824a9fe879699febc4e15e0b2067/themes/ArtFraming/images/white/Cart.gif') no-repeat 0 50%;
}

table.CartContents,
table.General {
    border: 1px solid #dadada;
}

table.CartContents thead tr th,
table.General thead tr th {
    background: #f4f4f4;
    color: #2f2f2f;
    border-bottom: 1px solid #dadada;
}

table.alignLeft td {
    text-align: left;
}

#cartForm > table > tbody > tr.Last {
    display: table-row !important;
}

.CartContents tfoot .SubTotal td {
    border-top: 1px solid #dadada;
    color: #606060;
}

.JustAddedProduct {
    border: 1px solid #dadada;
    background: #fff;
}

.JustAddedProduct strong,
.JustAddedProduct a {
    text-decoration: none;
}

.JustAddedProduct a:hover {
    text-decoration: underline;
}

.CategoryPagination {
    color: #606060;
    background: #f4f4f4;
    border: 1px solid #dadada;
}

.CheckoutButton a:hover {
    cursor: pointer;
}

.CheckoutRedeemCoupon {
    background: #f4f4f4;
    border: 1px solid #dadada;
    padding: 10px;
    width: 300px;
    border: 1px solid #dadada;
    margin-bottom: 10px;
}

.SubCategoryList,
.SubBrandList {
    background: #f4f4f4;
    border: 1px solid #dadada;
}

.InfoMessage {
    background: #f4f4f4 url('//cdn3.bigcommerce.com/r-4f615c2ae007824a9fe879699febc4e15e0b2067/themes/ArtFraming/images/InfoMessage.gif') no-repeat 5px 8px;
    border: 1px solid #dadada;
    color: #606060;
}

.SpecificInfoMessage {
    color: #606060;
    background-color: #f8e4d1;
    border: 1px solid #dadada;
}

.SuccessMessage {
    background: #f4f4f4 url('//cdn3.bigcommerce.com/r-4f615c2ae007824a9fe879699febc4e15e0b2067/themes/ArtFraming/images/SuccessMessage.gif') no-repeat 5px 8px;
    border: 1px solid #dadada;
    color: #606060;
}

.ErrorMessage {
    background: #FFECEC url('//cdn3.bigcommerce.com/r-4f615c2ae007824a9fe879699febc4e15e0b2067/themes/ArtFraming/images/ErrorMessage.gif') no-repeat 5px 8px;
    border: 1px solid #dadada;
    color: #606060;
}

.ComparisonTable tr:hover td,
.ComparisonTable tr.Over td {
    background: #f4f4f4;
}

.QuickSearch {
    border: 1px solid #dadada
}

.QuickSearchHover td {
    background: #f4f4f4;
}

.QuickSearchAllResults a {
    background: url('//cdn3.bigcommerce.com/r-4f615c2ae007824a9fe879699febc4e15e0b2067/themes/ArtFraming/images/white/QuickSearchAllResultsButton.gif') no-repeat right;
}

.QuickSearchResult .Price {
    color: #ff3941;
}

.CreateAccount {
    border-right: 1px solid #dadada;
}

.SearchTips .Examples {
    background: #f4f4f4;
    border: 1px solid #dadada;
}

hr {
    background: #8C8374;
    border: #8C8374;
    width: 90%;
    margin: 20px auto 40px;
}

.Right hr {
    background: transparent;
    border: 0;
}

.MiniCart .SubTotal {
    color: #ff3941;
}


/* Shipping Estimation on Cart Contents Page */

.EstimateShipping {
    background: #f4f4f4;
    border: 1px solid #dadada;
}


/* Express Checkout */

.ExpressCheckoutBlock .ExpressCheckoutTitle {
    background: #0066cc;
    color: #000;
}

.ExpressCheckoutBlockCollapsed .ExpressCheckoutTitle {
    background: #f4f4f4;
    color: #fff;
    border: 1px solid #dadada;
}

.ExpressCheckoutBlockCompleted .ExpressCheckoutCompletedContent .ExpressCheckoutBlockCollapsed {
    color: #fff;
}


/* General Tables */

table.General tbody tr td {
    border-top: 1px solid #dadada;
}


/* Tabbed Navigation */

.TabNav {
    border-bottom: 1px solid #dadada;
}

.TabNav li a {
    border: 1px solid #dadada;
    background: #f4f4f4;
    color: #606060;
}

.TabNav li a:hover {
    background-color: #dadada;
}

.TabNav li.Active a {
    border-bottom: 1px solid #dadada;
    background: #2f2f2f;
    color: #fff;
}


/* Product Videos */

#VideoContainer {
    border: 1px solid #cccccc;
    -moz-border-radius: 5px;
    -webkit-border-radius: 5px;
    border-radius: 5px;
}

.currentVideo {
    background-color: #ddf0f6;
}

.videoRow:hover {
    background-color: #efefef;
}

.CartContents .ItemShipping th {
    border-bottom-color: #dadada;
    border-top-color: #dadada;
    background: #f4f4f4;
    color: #2f2f2f;
}

#SideCategoryList .sf-menu .sfHover a,
#SideCategoryList .sf-menu li ul a {
    background-color: #ebebeb;
    color: #000;
}


/* Parent menu items of a child being hovered (not including root-level items) */

#SideCategoryList .sf-menu li ul li:hover>a,

/* Child menu item while hovering */

#SideCategoryList .sf-menu li ul a:focus,
#SideCategoryList .sf-menu li ul a:hover,
#SideCategoryList .sf-menu li ul a:active {
    background-color: #ebebeb;
}

#SideCategoryList .sf-sub-indicator {
    background-image: url('//cdn3.bigcommerce.com/r-4f615c2ae007824a9fe879699febc4e15e0b2067/themes/ArtFraming/images/white/arrows-dark.png');
}


/* End: category flyout styles */


/* start fast cart styles */

#fastCartContainer {
    font-family: "Trebuchet MS", Arial, Helvetica, sans-serif;
    font-size: 13px;
}

.fastCartContent h2 {
    color: #009E0F;
    background-color: transparent;
}

.fastCartContent h3 {
    color: #BB6F21;
    background-color: transparent;
}

.fastCartSummaryBox {
    background-color: #DDFEDA;
}

.fastCartContent .fastCartTop a,
.fastCartContent .ProductList .ProductDetails a {
    color: #075899;
}

.fastCartContent .ProductPrice,
.fastCartContent .ProductList em {
    color: #800000;
}

.fastCartContent .Quantity {
    color: #828DB3;
}

.fastCartContent p {
    color: #444444;
}

.fastCartContent .ProductList li {
    background-color: #FFF;
}


/* end fast cart styles */


/* begin social sharing styles */

.sharebutton {
    background: #0083d8;
    background-image: linear-gradient(top, #00A2E2 17%, #0073D1 84%);
    background-image: -o-linear-gradient(top, #00A2E2 17%, #0073D1 84%);
    background-image: -moz-linear-gradient(top, #00A2E2 17%, #0073D1 84%);
    background-image: -webkit-linear-gradient(top, #00A2E2 17%, #0073D1 84%);
    background-image: -ms-linear-gradient(top, #00A2E2 17%, #0073D1 84%);
    background-image: -webkit-gradient( linear, left top, left bottom, color-stop(0.17, #00A2E2), color-stop(0.84, #0073D1));
    border: none;
    cursor: pointer;
    padding: 2px 10px 2px 10px;
    -webkit-border-radius: 0px;
    -moz-border-radius: 0px;
    border-radius: 0px;
}

.sharebutton a,
.sharebutton a:hover {
    font-weight: bold;
    text-decoration: none;
    text-transform: uppercase;
    color: white;
    font-size: 11px;
}


/* end social sharing styles */


/* Duck Soup Customizations */

body {
    background: #fff;
    font-family: 'Open Sans', arial, helvetica, sans-serif;
    font-weight: 400;
}

#Container {
    width: 100%;
}

#TopMenu {
    width: 100%;
    display: inline-block;
    margin: 0px;
    background: #22448b none repeat scroll 0 0;
    height: 40px;
    -moz-box-shadow: 1px 0 2px #000000;
    -webkit-box-shadow: 1px 0 2px #000000;
    box-shadow: 1px 0 2px #000000;
     border-bottom: 4px solid #004389;


}

#TopMenu ul {
    margin: 0px auto;
    width: 1000px;
    float: none;
}

#TopMenu li {
    float: right;
}

li.LiveChat,
li.Phone {
    float: left !important;
}

#TopMenu li a,
li.Phone,
#TopMenu li div {
    color: #ffffff;
    font-size: 15px;
    margin: 5px 0;
    font-weight: 600;
    font-family: 'Open Sans', arial, helvetica, sans-serif;
}

li.Phone {
    margin-top: 12px !important;
}

#TopMenu li.CartLink a {
    padding-right: 0px;
    display: inline-block;
    background: url(https://www.savvyboater.com/content/images/homepage/cart-icon.png) no-repeat scroll left 1px transparent;
    margin: 0px;
    padding: 5px 0px 5px 30px;
}

#TopMenu li.CartLink span {
    display: inline-block;
    color: #C8C1B9;
    font-size: 15px;
    font-weight: bold;
}

#Outer {
    width: 1000px;
    margin: 0px auto;
}

#Header {
    /* background: url("https://www.savvyboater.com/content/images/homepage/Savvy-holiday-background.jpg"); */
    background: #084586;
    border: 0px;
    width: 950px;
    margin: 0px;
    padding: 50px 25px 0px;
    /* border-bottom: 2px solid #ffffff; */
    height: 95px;
}

#Menu {
    background: #F5F2EB;
    width: 1000px;
    margin: 0px;
    height: 30px;
    border-bottom: 2px solid #BBB6AE;
}

#Menu ul {
    text-align: center;
}

#Menu li {
    display: inline-block;
    border-left: 2px solid #BBB6AE;
