}

#Menu li.Last ul li a {
    background: #F5F2EB;
    border-left: 2px solid #BBB6AE;
    border-right: 2px solid #BBB6AE;
    width: 160px;
}

#SearchForm {
    width: 300px;
    height: 30px;
    margin: 0;
    position: relative;
    float: right;
}

#SearchForm img {
    margin: -8px 0px 14px 50px;
}

#SearchForm .Button {
    height: 20px;
    margin: 0 0 0 0px;
    width: 20px;
    max-height: 30px;
    position: absolute;
    top: 57px;
    right: 45px;
    padding: 0;
    /* background: transparent url('link_to_your_btn_image') 0 0 no-repeat; */
}

#FreeShippingIcon {
    /* background: #9c1b21; */
    padding: 2px 7px 4px 14px;
    width: 200px;
    /* border-radius: 2px; */
    /* shippingIconBackground { */
    width: 200px;
    height: 34px;
    padding: 2px;
    /* background: #9c1b21; */
    display: inline-block;
    /* border-radius: 2px; */
    margin: 2px 0px 10px 70px;
}

#FreeShippingIcon a {
    text-decoration: underline;
    color: #fff;
    font-weight: 700;
    font-size: 24px;
    font-style: italic;
}

main,
#Wrapper {
    width: 1000px;
}

.Content.Widest {
    margin: 0 25px;
    width: 950px;
}

.Homepage #Wrapper {
    padding-top: 0px;
}

.Homepage .Content.Widest {
    width: 1000px;
    margin: 0px;
}

.Homepage h3 {
