    font-size: 14px;
}

.Content.Wide.WideWithLeft {
    margin: 0 0 0 2px;
    width: calc(100% - 260px);
}

#CategoryHeading {
    margin-bottom: 0px;
}

.CategoryDescription {
    padding: 0;
}

h1,
.CategoryDescription h1 {
    color: #9A1D21;
    font-size: 24px;
    text-align: center;
    margin-bottom: 10px;
}

.CategoryDescription p {
    font-size: 15px;
    line-height: 1.5;
    padding: 0 40px;
    /* text-align: center; */
    margin-bottom: 0px;
}

.CategoryDescription a {
    color: #22448b;
}

.CategoryDescription a:hover {
    color: #9C1B21;
}

.CategoryPagination {
    background: none transparent;
    border: 0 solid #DADADA;
    color: #000000;
    padding-right: 0px;
    padding-top: 0px;
    margin-bottom: 0px;
    font-size: 15px;
    padding-bottom: 0px;
    display: inline-block;
    font-weight: 600;
}

.CategoryPagination .FloatRight a,
.CategoryPagination .FloatLeft a {
    color: #000000;
    font-weight: 600;
    text-decoration: none;
}

.CategoryPagination a {
    color: #000000;
    font-weight: 600;
    text-decoration: none;
}

#CategoryPagingTop {
    margin-top: 1px;
    float: none;
    text-align: center;
    width: auto;
    clear: right;
    margin-bottom: 5px;
}

.CategoryPagination .PagingList {
    float: right;
    margin: 0px 5px 0 0;
}

.CategoryPagination .PagingList li {
    padding: 0 3px;
}

.CategoryPagination .PagingList li.Label {
    color: #9A1D21;
    padding: 0;
}

.CategoryPagination .PagingList li.ActivePage {
    text-decoration: underline;
}

#CategoryPagingBottom {
    overflow: auto;
    text-align: center;
}

.Content .ProductList {
    clear: both;
}

.Content .ProductList li {
    float: left;
    font-size: 1em;
    list-style: none outside none;
    margin: 0;
    min-height: 25em;
    overflow: hidden;
    text-align: center;
    width: 210px !important;
    padding: 20px 17px;
}

.Content .ProductList .ProductImage {
    width: 210px !important;
}

.Content .ProductList .ProductDetails {
    margin: 0 8px;
}

.ProductDetails {
    min-height: 54px !important;
}

.ProductList strong,
.ProductList strong a {
    color: #1e1e1e;
    font-size: 13px;
}

.prodListSp1 {
    color: #9C1B21;
    font-weight: 700;
    font-size: 14px;
}

.prodTitleSp1,
.prodDescTitleSp1 {
    color: #000000;
    font-weight: 700;
}

.prodDescTitleSp2 {
