.HomeBottomImg p:nth-child(2) {
    position: absolute;
    font-size: 35px;
    color: #FFF;
    font-weight: 700;
    line-height: 1.4;
    text-align: center;
    left: 73px;
    top: 9px;
    text-shadow: 1px 3px 1px #000;
}

.HomeBottomImg p:nth-child(3) {
    font-size: 24px;
    font-weight: 700;
    text-align: center;
    padding: 5px;
    position: absolute;
    left: 154px;
    top: 183px;
    border: 3px solid #084586;
    border-radius: 10px;
    background-color: #fff;
}

.HomeBottomImg a {
    color: #084586;
    text-decoration: none;
    padding: 0px 5px;
    font-weight: 700;
}

.HomeBottomImg p {
    font-size: 20px;
    color: #fff;
    font-weight: 700;
    line-height: 1;
    text-align: center;
    position: absolute;
    left: 123px;
    top: 233px;
}

.HomeBottomImg a::after {
    content: '';
    position: absolute;
    border: none;
    top: -3px;
    right: -3px;
    bottom: -3px;
    left: -3px;
    -webkit-transition-duration: 0.2s;
    transition-duration: 0.2s;
    -webkit-transition-property: top, right, bottom, left;
    transition-property: top, right, bottom, left;
}

.HomeBottomImg a:hover::after,
.HomeBottomImg a:focus::after,
.HomeBottomImg a:active::after {
    border: 3px solid #fff;
    top: -6px;
    right: -6px;
    bottom: -6px;
    left: -6px;
    border-radius: 12px;
}

