.ac-container{
    width: 100%;
    margin: 10px auto 30px auto;
    text-align: left;
}
.ac-container label {
    padding: 5px 20px;
    position: relative;
    z-index: 20;
    display: block;
    //cursor: pointer;
    color:  #22448b;
    line-height: 33px;
    font-size: 18px;
        font-weight: bold;
    background: #eaeff3;
        border: 1px solid #b0bdc3;
        margin-top: 15px;
}
.ac-container input:checked + label,
.ac-container input:checked + label:hover{
}
.ac-container label:after,
.ac-container input:checked + label:hover:after{
    content: '\3009';
    position: absolute;
    width: 24px;
    height: 24px;
    right: 6px;
    top: 16px;
    background: transparent;
    color: #22448b;
    -ms-transform: rotate(90deg);
    -webkit-transform: rotate(90deg);
    transform: rotate(90deg);
    font-size: 24px;
    font-weight: bold;
}
.ac-container input:checked + label:hover:after{
    -ms-transform: rotate(-90deg);
    -webkit-transform: rotate(-90deg);
    transform: rotate(-90deg);
}
.ac-container input{
    display: none;
}
.ac-container article{
    background: rgba(255, 255, 255, 0.5);
    margin-top: -1px;
    overflow: hidden;
    height: 0px;
    position: relative;
