/* Icon Down Before */

@-webkit-keyframes hvr-icon-down {
    0%,
    50%,
    100% {
        -webkit-transform: translateY(0);
        transform: translateY(0);
    }
    25%,
    75% {
        -webkit-transform: translateY(6px);
        transform: translateY(6px);
    }
}

@keyframes hvr-icon-down {
    0%,
    50%,
    100% {
        -webkit-transform: translateY(0);
        transform: translateY(0);
    }
    25%,
    75% {
        -webkit-transform: translateY(6px);
        transform: translateY(6px);
    }
}

.hvr-icon-down,

.hvr-icon-down-after {

    display: inline-block;
    vertical-align: middle;
    -webkit-transform: translateZ(0);
    transform: translateZ(0);
    box-shadow: 0 0 1px rgba(0, 0, 0, 0);
    -moz-box-shadow: 0 0 1px rgba(0, 0, 0, 0);
    -webkit-box-shadow: 0 0 1px rgba(0, 0, 0, 0);
    -webkit-backface-visibility: hidden;
    backface-visibility: hidden;
    -moz-osx-font-smoothing: grayscale;
    position: relative;
    padding-right: 2.2em;
    text-align: center;
}

#landingPage a.hvr-icon-down {
    margin-left: 90px;
    text-decoration: none;
    font-size: 18px;
}

.LaddersCategoryDescription a.hvr-icon-down-after,
.FendersCategoryDescription a.hvr-icon-down-after,
.SeatsCategoryDescription a.hvr-icon-down-after,
.DeicerCategoryDescription a.hvr-icon-down-after {
    text-decoration: none;
    font-weight: 600;
    padding: 0px 25px 0px 0px;
}

.hvr-icon-down:before {
    content: "\25BC";
    position: absolute;
    right: 13.4em;
    padding: 0 1px;
    -webkit-transform: translateZ(0);
    transform: translateZ(0);
}

.hvr-icon-down-after:after {
    font-family: Arial;
    content: "\2193";
    position: absolute;
    right: 0px;
    -webkit-transform: translateZ(0);
    transform: translateZ(0);
}

.hvr-icon-down:hover:before,
.hvr-icon-down:focus:before,
.hvr-icon-down:active:before,
.hvr-icon-down-after:hover:after,
.hvr-icon-down-after:focus:after,
.hvr-icon-down-after:active:after {
    -webkit-animation-name: hvr-icon-down;
    animation-name: hvr-icon-down;
    -webkit-animation-duration: 0.75s;
    animation-duration: 0.75s;
    -webkit-animation-timing-function: ease-out;
    animation-timing-function: ease-out;
}


